INSERT INTO `strategy_detail` VALUES (2, 3261, 3, 1, '有娃必看，广州长隆野生动物园全攻略', '广州长隆野生动物园', '如果要说动物园，楼主强烈推荐带娃去广州长隆野生动物园！野生动物园有许多，但是广州长隆野生动物园只有一个！为什么长隆野生动物园如此与众不同呢？', '/wKgED1v7lwqAZm1mAAG55YkXRWI84.jpeg', '2018-11-26 00:00:00', 203, 45, 51, 23, 100, 0);
INSERT INTO `strategy_detail` VALUES (3, 3261, 6, 2, '广州小吃名店红黑榜：你还是当年珠江畔那个老字号吗？', '广州小吃名店红黑榜', '企鹅吃喝指南｜“城市指南“第4站－广州   小吃篇', '/wKgB3FEsZt6AaeAdAARfi3uw2CU10.jpeg', '2019-06-06 00:00:00', 89, 26, 20, 54, 6, 0);
INSERT INTO `strategy_detail` VALUES (4, 3261, 6, 3, '吃20cm的油炸大蜈蚣、⑥种昆虫！五毒之首到底什么味道？', '五毒之首到底什么味道？', '这是一个悲伤的故事，那天，我约瞎瞎出来吃饭……剩我独自一人挑战1%的广州人才敢吃的虫！提示：前方高能，胆小勿入！', '/wKgBEFtYAbaAZ-Z4AAdEuJZ3y5o22.jpeg', '2019-03-04 00:00:00', 94, 94, 63, 25, 105, 0);
INSERT INTO `strategy_detail` VALUES (5, 249, 14, 4, '上海十大人气景点，你都去过吗？', '上海十大人气景点', '上海素有“魔都”之称，一直是国内繁华都市的代表。根据马蜂窝用户出行数据，以下是最受欢迎的10个上海景点。这些景点是上海的城市精华，适合初次造访想要打卡景点的游客', '/wKgBZ1nlfSaAUDRhAAcwkdSo3Hk98.jpeg', '2017-06-14 00:00:00', 31, 82, 15, 7, 30, 0);
INSERT INTO `strategy_detail` VALUES (6, 249, 3, 5, 'N刷迪士尼，一篇干货满满的上海迪士尼攻略', 'N刷上海迪士尼', '买了两次迪士尼季卡，N刷迪士尼，迪士尼迷妹为大家总结的一份上海迪士尼攻略。', '/wKgED1wL1WyAfh6wAAIJgBDsRpA21.jpeg', '2018-12-10 00:00:00', 47, 11, 17, 99, 50, 0);
INSERT INTO `strategy_detail` VALUES (7, 249, 6, 6, '懒人吃货指南 | 轻松吃遍地道上海小吃', '轻松吃遍地道上海小吃', '去一个地方旅行当然要尝尝当地的特色美食，不然总觉得这趟旅行不完整。但是对于不熟悉上海的游客来说，“哪些是老上海小吃？去哪里能吃到地道的？”这些都是头疼的问题。这篇攻略将会给大家介绍老上海小吃，推荐一些老字号，并附上周边游玩小Tips', '/wKgBEFrkRVCAUV_ZAAWtxoKD0bU28.jpeg', '2018-05-18 00:00:00', 48, 42, 65, 13, 2, 0);
INSERT INTO `strategy_detail` VALUES (8, 249, 13, 6, '上海海昌海洋公园一日游攻略', '上海海昌海洋公园一日游', '被高铁站的广告吸引到了海昌海洋公园，不得不说，这是我在上海玩过的最有趣的海洋公园。', '/wKgED1wHkciAaj24AAuSFkxN7zE24.jpeg', '2018-12-05 00:00:00', 61, 66, 49, 93, 48, 0);
INSERT INTO `strategy_detail` VALUES (9, 241, 15, 7, '北京逛故宫全技能Get！', '北京逛故宫全技能Get！', '北京土著说：我逛不逛，它都在那儿，我想啥时候逛就啥时候逛！外地游客说：来到大北京，必须逛故宫！常住在北京的外地人说：老家人来了N次，故宫已经逛了N次！不管你是从没逛过，还是第一次逛，还是正要第N+1次逛，逛故宫的技能你都get了吗？', '/wKgBEFp319-AAngOAANweV_VNTM54.jpeg', '2016-09-07 00:00:00', 21, 20, 40, 95, 43, 0);
INSERT INTO `strategy_detail` VALUES (10, 241, 15, 7, '周末去爬八达岭长城', '八达岭长城路线', '八达岭长城，位于北京市西北部的延庆区，是旧时军都山关沟古道；作为万里长城的一部分，这个建于明代的长城是最早向游人开放的地段，所以整个景区的餐饮、娱乐、交通等配套设施较为完善。', '/wKgBZ1ny08-AalUvAAkEbRsgv2811.jpeg', '2017-10-27 00:00:00', 47, 49, 5, 87, 81, 0);
INSERT INTO `strategy_detail` VALUES (11, 241, 15, 8, '玩遍大北京！你绝不可错过的景点', '玩遍大北京', '北京是一座迷人的城市，小胡同、老茶馆、新潮酒吧街、繁华商业区，无限的摩登元素与老北京地道的京味儿相互交融，构筑了北京城博大精深的文化底蕴和正统而不失清雅的生活方式。来北京必去的景点非天安门、故宫、长城莫属，除此以外，可根据各人的兴趣爱好进行多样化的选择。不论是情侣出行还是亲子旅游，向往皇家古迹还是时尚街区，都能在北京找到最适合的去处。', '/wKgEaVyLc4WAANXaAAPRi-cmc7I74.jpeg', '2016-10-24 00:00:00', 115, 16, 100, 21, 26, 0);
INSERT INTO `strategy_detail` VALUES (12, 241, 15, 9, '京城十大胡同｜不当一回胡同串子，你算白来北京了 ！', '京城十大胡同', '有人说，胡同是老北京人的魂儿，一天不见就像丢了魂儿。但对大多数每天匆匆穿梭于北京大街小巷的人们而言，胡同还只是一个模糊的概念，是一个越来越稀有的存在。 春日的北京，阳光明媚，充满无限生机（就不提霾这事了）。掸一掸车座上的土，骑着俩轱辘逛一逛北京城，当一回胡同串子，拍一拍北京的犄角旮旯，才是最北京的玩法', '/wKgBZ1i5OImAQyseAACzRyMASOo96.jpeg', '2017-03-03 00:00:00', 47, 71, 15, 62, 61, 0);
INSERT INTO `strategy_detail` VALUES (13, 3148, 7, 10, '杭州旅行全攻略', '杭州旅行全攻略', '上有天堂，下有苏杭”这座自古就被人称作“人间天堂”的杭城，山、泉、湖、桥、塔、寺样样俱全，世人对于杭州的印象无论是自然的湖光山色还是充满历史感的名胜古迹都充满了溢美之词。', '/wKgEaVxve8-AKu2MABICGVQUWZA657.gif', '2019-02-22 00:00:00', 29, 53, 80, 79, 44, 0);
INSERT INTO `strategy_detail` VALUES (14, 2030, 6, 11, '日本好吃不贵的排队美食大全!（含东京、大阪、京都）', '日本美食', '每到一个日本城市，最先关心的不是当地名胜，不是各种卖场，一定要先尝尝当地最具代表的排队美食！！汤头诱人的拉面、爆炸味蕾的海鲜、鲜美精致的寿司…日本美食讲究使用新鲜、应季的食材，运用传统的手法，烹调出匠人追求的味道。下面这些人气平价美食攻略是经我亲测的良心推荐，它们通常是需要预约或排队的，但你绝对不可错过⊙ω⊙！', '/wKgBs1b56W2ARtseAAo2CKssH5410.jpeg', '2016-03-24 00:00:00', 82, 85, 41, 51, 18, 0);
INSERT INTO `strategy_detail` VALUES (15, 350, 7, 12, '百老汇观剧指南丨纽约一夜', '纽约一夜', '如果说去纽约最值得做的事，去百老汇看场剧必在其中！旅途中若缺少了精神食粮，怎能让一段旅程变得特别而难以忘怀？不关乎艺术的晦涩难懂，亦不关乎语言的不通障碍。当音乐响起，帷幕拉开，只需用心感受，你将领会故事本身和对于自己的意义。', '/wKgBZ1jZy5WAZ-kuAAIRkV9wxZE80.jpeg', '2017-03-28 00:00:00', 27, 80, 22, 83, 70, 0);
INSERT INTO `strategy_detail` VALUES (16, 193, 10, 13, '第一次去泰国，一定要了解的30个问题', '第一次去泰国问题', ' 中国大陆居民可凭有效护照提前办理泰国旅游签证，签证费用为230元人民币。同时，无论是从国内直接出发，还是从第三国或地区到达泰国，中国公民均可凭至少还有6个月有效期的护照办理有效期为15天的落地签证。', '/wKgBEFtnuiGARrzwAAFasxKupZE08.jpeg', '2018-08-06 00:00:00', 5, 79, 79, 66, 61, 0);
INSERT INTO `strategy_detail` VALUES (17, 3356, 16, 14, '成都周边一日游，去都江堰和青城山吸氧避霾', '成都周边一日游', '拜水都江堰，问道青城山。多少人冲着余秋雨先生这句话来到成都！这篇攻略就是要告诉你如何玩转都江堰、青城山，看看游客必玩的打卡地如何玩出本地人爱的local感。', '/wKgEaVyPWKKAemSkAAZduoAFlWs20.jpeg', '2017-07-27 00:00:00', 47, 38, 48, 3, 30, 0);
